from Crypto.Util.number import *

m = bytes_to_long(b'ACTCTF{30da0f0dbf1432ef94cd2eb6ce04ec28}')
p = getPrime(512)
q = getPrime(512)
e = 65537
n = p*q
c = pow(m, e, n)
print(f'notient = ', n)
print(f'exponent = ', e)
print(f'cipher = ', c)
print(f'p+q = ', p + q)

